import React from "react";
import L from 'leaflet';



const InfoBox = (props) => {
    console.log(props);
    this._div = L.DomUtil.create('div'); // create a div with a class "info"

    this._div.innerHTML = '<h3>Коренные малочисленные  <br/> народы  Севера, Сибири <br/> и Дальнего Востока России</h3><br />' +  ( props.info ?
        '<h4>' + props.info.Nation + '</h4>' + '<br />' + '<h6> Население</h6>' + props.info.Population + ' человек ' + '<br /><br />' + '<h6>Язык</h6>' + props.info.Language + '<br /><br />' ///+ '<h6>Языковая семья</h6>' + props.LangFamily  +'<br /><br />'  ///+ '<h6>Регионы проживания</h6>' + props.Oblast
        : 'Нажмите на народ');


    return (
        <div className = "infobox">
            <div className = "title" ><h4>Коренные малочисленные <br/> народы  Севера, Сибири <br/> и Дальнего Востока России</h4><br /></div>
            this._div
            <div className = "buttondiv"><button className="myButton">ИГРАТЬ</button></div>    
        </div>
    );
  };


export default InfoBox;
