import React, { useState } from "react";
import { selectedNations } from "./quizConstants";


const InfoBox = (props) => {
    console.log(props);

    const [quizMode, setQuizMode] = useState(false);
    const [learnMode, setLearnMode] = useState(false);
    const [startMode, setStartMode] = useState(true);
