import React from "react";




const InfoBox = (props) => {
    console.log(props);

var mainInfo = props.info.Nation +  '<h1>Население: </h1>';


    return (
        <div className = "infobox">
            <div className = "title" ><h4>Коренные малочисленные <br/> народы  Севера, Сибири <br/> и Дальнего Востока России</h4><br /></div>
            <div className = "text">Информация о народе: {props.info ? mainInfo : "Выберите область"}</div>  
            <div dangerouslySetInnerHTML={{ __html: "Привет"  }} />
            <div className = "buttondiv"><button className="myButton">ИГРАТЬ</button></div>    
        </div>
    );
  };


export default InfoBox;
