import React from "react";
import { useRef, useState } from "react";
import L from "leaflet";
import { GeoJSON } from "react-leaflet";
import * as myConstClass from "./constants.js";
import nations from "../data/nations.json";

export default function PeoplesMapStart() {
  let selected = null;

  const HighlightFeature = (layer) => {
    if (selected == null || selected._leaflet_id !== layer._leaflet_id) {
      layer.setStyle({
        weight: 1.5,
        dashArray: "",
        fillOpacity: 0.8,
      });
      if (!L.Browser.ie && !L.Browser.opera && !L.Browser.edge) {
        layer.bringToFront();
      }
    }
  };

  const geoJsonRef = useRef();

  const ResetHighlight = (layer) => {
    if (selected == null || selected._leaflet_id !== layer._leaflet_id) {
      geoJsonRef.current.resetStyle(layer);
    }
  };

  const Select = (layer) => {
    if (selected !== null) {
      var previous = selected;
    }
    layer.setStyle({
      weight: 3,
      color: "white",
      fillOpacity: 1,
    });

    selected = layer;
    if (previous) {
      ResetHighlight(previous);
    }
  };

  const onEachFeatureF = (feature, layer) => {
    layer.on({
      mouseover: function (e) {
        HighlightFeature(e.target);
      },
      mouseout: function (e) {
        ResetHighlight(e.target);
      },
    });
  };
  return (
  <GeoJSON
      data={nations}
      style={myConstClass.subteStyle}
      onEachFeature={onEachFeatureF}
      ref={geoJsonRef}
    />
  );
}
