import React from "react";
import { useRef, useEffect} from "react";
import L from "leaflet";
import { GeoJSON } from "react-leaflet";
import * as myConstClass from "./constants.js";
import nations from "../data/nations.json";

export default function PeoplesMapQuiz(props) {
  let selected = null;

  const HighlightFeature = (layer) => {
    if (selected == null || selected._leaflet_id !== layer._leaflet_id) {
      layer.setStyle({
        weight: 1.5,
        dashArray: "",
        fillOpacity: 1,
      });
      if (!L.Browser.ie && !L.Browser.opera && !L.Browser.edge) {
        layer.bringToFront();
      }
    }
  };

  const geoJsonRefQuiz = useRef();

  const ResetHighlight = (layer) => {
    if (selected == null || selected._leaflet_id !== layer._leaflet_id) {
      geoJsonRefQuiz.current.resetStyle(layer);
    }
  };

  useEffect(() => {
    const isRight = props.isRight
    return(isRight)
  }, [props.isRight]);


  const Select = (layer) => {
    if (selected !== null) {
      var previous = selected;
    }
    console.log(isRight)
    if (isRight) {
      layer.setStyle({
        weight: 3,
        color: "#A4D6A5",
        fillOpacity: 0.5,
        fillColor: "green",
      });
    } else {
      layer.setStyle({
        weight: 1,
        color: "white",
        fillColor: "#F27272",
        fillOpacity: 0.5,
      });
    }

    selected = layer;
    if (previous) {
      ResetHighlight(previous);
    }
  };

  const onEachFeatureFQuiz = (feature, layer) => {
    layer.on({
      click: function (e) {
        Select(e.target);
        props.handleSetAnswer(e.target.feature.properties.Nation);
        layer
          .bindTooltip(feature.properties.Nation, {
            direction: "center",
            position: "auto",
          })
          .openTooltip();
      },
      mouseover: function (e) {
        HighlightFeature(e.target);
      },
      mouseout: function (e) {
        ResetHighlight(e.target);
      },
    });
  };
  return (
    <GeoJSON
      data={nations}
      style={myConstClass.quizStyle}
      onEachFeature={onEachFeatureFQuiz}
      ref={geoJsonRefQuiz}
    />
  );
}
